twitter_handles.each do |handle|
    if handle.length < shortest_handle.length
        shortest_handle = handle
    end
end
puts "le handle le plus court est " + shortest_handle.to_s 

#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)
n=0
twitter_handles.each do |handle|
    if handle.length == 6
        n = n+1    
    end
end 
puts "il y a " + n.to_s + " handles contenant 5 caractères (le @ ne compte pas)"

#how many handles start with an uppercase after the @ ?
n=0
twitter_handles.each do |handle|
    if handle[1] == handle[1].upcase
        n = n+1 
    end 
end 
puts "il y a " + n.to_s + " handles qui commencent par une majuscule (après le @)"

#Trie la liste de handle par ordre alphabétique // sort! => sort the array 
